import React, { Component } from 'react'
import UserServer from "../../server/UserServer"

export default class SignUp extends Component {
    constructor(props) {
        super(props)

        this.state = {
            email: "",
            password: "",
            password_confirmation: ""
        }
    }

    userInputHandle = (event) => {
        this.setState({ [event.target.name]: event.target.value });
    }


    render() {
        return (

            <div>
                <br></br>
                <div className="conainer">
                    <div className="row">
                        <div className="card col-md-6 offset-md-3 offset-md-3">
                            <h3 className="text-center" style={{ marginTop: "30px" }}>Add Student</h3>
                            <div className="card-body">
                                <form>

                                    {/* INPUT NAME */}
                                    <div className="form-group">
                                        <label htmlFor="exampleInpuEmail">Email</label>
                                        <input type="text" placeholder="Email" name="email" className="form-control"
                                            value={this.state.email} onChange={this.handleInputChange} />
                                    </div>


                                    {/* INPUT EMAIL */}
                                    <div className="form-group">
                                        <label htmlFor="exampleInpuEmail">Email</label>
                                        <input placeholder="Email" name="email" className="form-control"
                                            value={this.state.email} onChange={this.handleInputChange} />
                                    </div>


                                    {/* INPUT AGE */}
                                    <div className="form-group">
                                        <label htmlFor="exampleInpuAge">Age</label>
                                        <input placeholder="Age" name="age" className="form-control"
                                            value={this.state.age} onChange={this.handleInputChange} />
                                    </div>


                                    <button className="btn btn-success" onClick={this.createUser}>Save <FaSave size={15} /> </button>
                                    <button className="btn btn-danger" onClick={this.cancelButton.bind(this)} style={{ marginLeft: "10px" }}>Cancel <MdCancel size={15} /></button>



                                </form>
                            </div>
                        </div>
                    </div>
                </div>
            </div>

        )
    }
}
